void test_mul(Cryptosystem & cs)
{
    cout << "Time (mul) :: " << std::flush;
    const int niter = 1;

    Timer tmr1;
    SecureInt a1("",cs);
    SecureInt b1("",cs);
    for ( int ctrl=0; ctrl<niter; ctrl++ ) a1 += b1;
    auto tv1 = tmr1.get();
    cout << "SecureInt = " << (tv1/niter) << " , " << std::flush;;

    Timer tmr2;
    Unumber a2("");
    Unumber b2("");
    Unumber c2(cs.getN2());
    for ( int ctrl=0; ctrl<niter; ctrl++ )
    {
        Unumber aux = a2.mul(b2, c2);
    }
    auto tv2 = tmr2.get();
    cout << "Unumber = " << (tv2/niter) << " , " << std::flush;;

    Timer tmr3;
    mpz_t a3, b3, c3;
    mpz_init_set_str(a3, "",10);
    mpz_init_set_str(b3, "",10);
    mpz_init_set_str(c3,cs.getN2().str().c_str(),10);
    for ( int ctrl=0; ctrl<niter; ctrl++ )
    {
        mpz_mul(a3, a3, b3);
        mpz_mod(a3, a3, c3);
    }
    auto tv3 = tmr3.get();
    cout << "GMP = " << (tv3/niter) << "\n";
}

