void test_fact(Cryptosystem & cs)
{
#ifdef COPHEE
    Unumber::resetCounters();
#endif
    cout << "Factorial\n";
    const int MAX_NUM = 10;
    SecureInt num("",cs);
	SecureInt fact("",cs);
	SecureInt i("",cs);
	SecureInt result("",cs);

    Timer tmr;
	int counter = 1;
	do {
		fact *= i;
		result += (i == num) * fact;
		++i;
	} while (++counter != MAX_NUM);
    auto tv = tmr.get();
    std::cout << "Time: " << tv << "\n";
#ifdef COPHEE
    Unumber::showTimes();
#endif

	cout << "fact( " << num.str() << " ) = " << result.str() << "\n";
}

void test_fib(Cryptosystem & cs)
{
#ifdef COPHEE
    Unumber::resetCounters();
#endif
    cout << "Fibonacci\n";
    const int MAX_NUM = 10;
    SecureInt num("",cs);
	SecureInt f1("",cs);
	SecureInt f2("",cs);
	SecureInt fi("",cs);
	SecureInt i("",cs);
	SecureInt result("",cs);

    Timer tmr;
	int counter = 1;
	do {
		result += (i == num) * fi;
		fi = f1 + f2;
		f1 = f2;
		f2 = fi;
		++i;
	} while (++counter != MAX_NUM);
    auto tv = tmr.get();
    std::cout << "Time: " << tv << "\n";
#ifdef COPHEE
    Unumber::showTimes();
#endif

	cout << "fib( " << num.str() << " ) = " << result.str() << "\n";
}

void test_isort(Cryptosystem & cs)
{
#ifdef COPHEE
    Unumber::resetCounters();
#endif
    cout << "Insertion sort\n";
    const size_t LEN = 4;
    SecureInt array[LEN];
    int i, j;
    array[0] = SecureInt("",cs);
    array[1] = SecureInt("",cs);
    array[2] = SecureInt("",cs);
    array[3] = SecureInt("",cs);

    Timer tmr;
    SecureInt x, y, cond1, cond2;
    for (i = 1 ; i < LEN; i++)
    {
        j = i;
        while (j != 0)
        {
            x = array[j-1];
            y = array[j];

            cond1 = y <= x;
            cond2 = x < y;
            array[j-1] = cond1 * y + cond2 * x; //cond1.mux(y, x); // min
            array[j] = x + y - array[j-1]; //cond.mux(x, y); // max

            j--;
        }
    }
    auto tv = tmr.get();
    std::cout << "Time: " << tv << "\n";
#ifdef COPHEE
    Unumber::showTimes();
#endif

    for (i = 0; i < LEN; i++) cout << array[i].str() << " ";
    cout << std::endl;
}

void test_mm(Cryptosystem & cs)
{
#ifdef COPHEE
    Unumber::resetCounters();
#endif
    cout << "Matrix multiplication\n";
    SecureInt a[3][3], b[3][3], c[3][3];

    a[0][0] = SecureInt("",cs);
    a[0][1] = SecureInt("",cs);
    a[0][2] = SecureInt("",cs);
    a[1][0] = SecureInt("",cs);
    a[1][1] = SecureInt("",cs);
    a[1][2] = SecureInt("",cs);
    a[2][0] = SecureInt("",cs);
    a[2][1] = SecureInt("",cs);
    a[2][2] = SecureInt("",cs);

    b[0][0] = SecureInt("",cs);
    b[0][1] = SecureInt("",cs);
    b[0][2] = SecureInt("",cs);
    b[1][0] = SecureInt("",cs);
    b[1][1] = SecureInt("",cs);
    b[1][2] = SecureInt("",cs);
    b[2][0] = SecureInt("",cs);
    b[2][1] = SecureInt("",cs);
    b[2][2] = SecureInt("",cs);

    Timer tmr;
    for (int i = 0; i < 3; i++)
    {
        for (int j = 0; j < 3; j++)
        {
            c[i][j] = SecureInt("",cs);
            for (int k = 0; k < 3; k++) c[i][j] += a[i][k] * b[k][j];
        }
    }
    auto tv = tmr.get();
    std::cout << "Time: " << tv << "\n";
#ifdef COPHEE
    Unumber::showTimes();
#endif

    for (int i = 0; i < 3; i++)
    {
        for (int j = 0; j < 3; j++) cout << c[i][j].str() << " ";
        cout << "\n";
    }
}

SecureInt *pzero, *pone;
SecureInt *ptwo;

void test_soe(Cryptosystem & cs)
{
#ifdef COPHEE
    Unumber::resetCounters();
#endif
    cout << "Sieve of Eratosthenes\n";
	SecureInt zero=SecureInt("",cs), two=SecureInt("",cs);
	SecureInt bzero=SecureInt("",cs), bone=SecureInt("",cs);
	// calls string parser

	// SecureBool test_one(two); // calls reduction

	pzero = &bzero;
	pone = &bone;
	ptwo = &two;

	int n = 30;
	SecureInt prime[n+1];
	SecureInt ret[n+1];
	for (int i=0; i<n+1; i++) ret[i]=zero;

    Timer tmr;
	sieveOfEratosthenes(ret,prime,n);
    auto tv = tmr.get();
    std::cout << "Time: " << tv << "\n";
#ifdef COPHEE
    Unumber::showTimes();
#endif

	for (int p = 2; p < n+1; p++)
		cout << ret[p].str() << ' ';
	cout<<std::endl;
}


// A function to implement bubble sort
void bubbleSort(SecureInt arr[], int n)
{
	int i, j;
	SecureInt cond1, cond2, smaller, greater;

	for (i = 0; i < n-1; i++)
	{
		// Last i elements are already in place
		for (j = 0; j < n-i-1; j++)
		{
			cond1 = arr[j] <= arr[j+1];
			cond2 = arr[j+1] < arr[j];
			smaller = cond1 * arr[j] + cond2 * arr[j+1];
			greater = arr[j+1] + arr[j] - smaller;
			arr[j] = smaller;
			arr[j+1] = greater;
		}
	}
}

/* Function to print an array */
void printArray(SecureInt arr[], int size)
{
	for (int i=0; i < size; i++) cout << arr[i].str() << " ";
	cout << "\n";
}

void sieveOfEratosthenes(SecureInt ret[], SecureInt prime[], int n)
{
	for (int i = 0 ; i < n+1 ; i++)
        	prime[i] = *pone;

	for (int p = 2; p*p < n+1 ; p++)
	{
		cout<<' '<<p<<std::flush;
		for (int i = 2*p; i < n+1 ; i += p)	// Update all multiples of p
			prime[i] = *pzero;
	}
	cout<<'\n';

	SecureInt pe = *ptwo;
	for (int p = 2; p < n+1; ++pe, p++)
		ret[p] = pe * prime[p];
}
