#include <iostream>
#include "../../src/e3extensions/secureint.h"

using namespace std;

#define MAX_NUM 10
//#define NUM 5

string libgDir = "./libg.so";
string gFunctionName = "libg";

int main()
{
	Cryptosystem cs("",32,"",{"","","","","","","","","","","","","","","","","","","","","","","","","","","","","","","","",""},"","", libgDir, gFunctionName);

	SecureInt num("",cs);
	SecureInt fact("",cs);
	SecureInt i("",cs);
	SecureInt result("",cs);
	
	int counter = 1;

	do {
		//cout << counter << "...\n";
		fact *= i;
		result += (i == num) * fact;
		++i;
	} while (++counter != MAX_NUM);

	cout << "fact( " << num.str() << " ) = " << result.str() << "\n";

	return 0;
}

